import reduce from "https://deno.land/x/lodash/reduce.js"

export default async function logger(ctx, next) {
  const { method, url } = ctx.request
  const params = reduce(ctx.params || {}, (value, key, p) => `${p}, ${key}: "${value}"`, '')

  App.log.info(`Requesting ${method} "${url}" (Parameters: {${params}})`)

  await next()

  const time = ctx.response.headers.get("X-Response-Time");
  const status = ctx.response.status || 404

  App.log.info(`Responded with ${status} in ${time}`)
}

export default async function MissingRoute(context, next) {
  if (!context.response.body) {
    context.response.body = `No route matches ${context.request.url}`
    context.response.status = 404
  }

  App.log.warning(`No route matches ${context.request.url}`)

  await next()
}

/**
 * Redirect all insecure requests to HTTPS
 */
export default function* SSLRedirect(next) {
  if (this.secure) {
    return yield next
  }

  const host = this.request.header.host
  const path = this.request.url
  const url = `https://${host}/${path}`

  this.response.status = 301

  this.response.redirect(url)
}

export default async function(ctx, next) {
  const start = Date.now();
  await next();
  const ms = Date.now() - start;
  ctx.response.headers.set("X-Response-Time", `${ms}ms`);
}

