  }
}

import { Query } from "https://deno.land/x/sql_builder/mod.ts"
import each from "https://deno.land/x/lodash/each.ts"

/**
 * An extension of `SQLBuilder.Query`, Relations are used to both build
 * new queries from fragments and to return the results of that query
 * when it is needed.
 */
export default class Relation extends Query {
  constructor(model) {
    super()
    this.model = model
    this.table(this.model.table)
    this.filters = {}
  }

  /**
   * Compile this query to SQL.
   */
  get sql() {
    if (!this._fields) {
      this.select("*")
    }

    return this.build()
  }

  /**
   * Perform the compiled query specified by this object.
   */
  run() {
    const result = App.db.exec(this.sql)

    if (!Array.isArray(result)) {
      return result
    }

    rows.map(row => new this.model(row))
  }

  /**
   * Return the first record in the result set.
   */
  get first() {
    const records = this.run()

    return records[0]
  }

  /**
   * Return the last record in the result set.
   */
  get last() {
    const records = this.run()

    return records[records.length]
  }

  /**
   * Find the length of all returned records in the result.
   */
  get length() {
    return this.run().length
  }

  /**
   * Perform a SQL COUNT() query with the given parameters and return
   * the result.
   */
  get count() {
    this.select("count(*)")

    return this.run()
  }

  /**
   * Override where() from `SQLBuilder.Query` to save off all filters
   * specified the object.
   */
  where(query = {}, ...context) {
    if (typeof query === "string") {
      return super.where(query, ...context)
    }

    this.filters = { ...this.filters, ...query }

    each(query, (value, param) => {
      if (typeof value === "function") {
        super.where(param, ...value())
      } else {
        super.where(param, "=", value())
      }
    })

    return this
  }

  /**
   * Perform the query and iterate over all of its results.
   */
  forEach(iterator) {
    const records = this.run()

    return records.forEach(iterator)
  }

  /**
   * Perform the query and iterate over all of its results, returning a
   * new Array of each iteration's return value.
   */
  map(iterator) {
    const records = this.run()

    return records.map(iterator)
  }

  /**
   * Perform the query and iterate over all of its results, returning a
   * new memoized object based on the logic performed in each iteration.
   */
  reduce(iterator, memo) {
    const records = this.run()

    return records.reduce(iterator, memo)
  }

  /**
   * Perform the query and iterate over all of its results, returning a
   * new Array containing the records for which the iteration's return
   * value was truthy.
   */
  filter(iterator) {
    const records = this.run()

    return records.filter(iterator)
  }

  /**
   * Perform the query and iterate over all of its results, returning
   * `true` if the given record exists within it. The record's ID
   * property is used to evaluate this.
   */
  contains(record) {
    return this.map(record => record.id).contains(record.id)
  }

  /**
   * Create a new model that would appear in this query.
   */
  create(attributes={}) {
    return this.model.create({ ...attributes, ...this.filters })
  }

  /**
   * Instantiate a new model that would appear in this query.
   */
  build(attributes={}) {
    return new this.model({ ...attributes, ...this.filters })
  }
}

export class Validation {
  constructor({ property, ...options }) {
    this.property = property
    this.options = options
  }

  valid(model) {}
}

export class Presence extends Validation {
  valid(model) {
    const { property } = this.options
    const message = this.options.message || "must be present"

    if (typeof model[property] === "undefined") {
      model.errors.add(property, message)
    }
  }
}

export class GenericValidation extends Validation {
  valid(model) {
    const { method } = this.options
    const validation = model[method].bind(model)

    validation()
  }
}

// Validations that can be used as part of the `validates` method.
export default {
  presence: Presence
}

import reduce from "https://deno.land/x/lodash/reduce.js"
import merge from "https://deno.land/x/lodash/merge.js"
import Validations, { GenericValidation } from "./model/validations.js"
import Errors from "./model/errors.js"
import Relation from "./model/query.js"

/**
 * A macro for creating a new `Validation` object in the list of
 * validations a model may run through.
 */
export function validates(name, validations={}) {
  return map(validations, (name, options) => {
    const Validation = Validations[name]
    options = options === true ? {} : options

    return new Validation(options)
  })
}

/**
 * A macro for creating a new `GenericValidation` object, allowing the
 * validation to consist of just running a method which may or may not
 * add errors.
 */
export function validate(method, options={}) {
  return new GenericValidation({ method, ...options })
}

export default class Model {
  static validations = []
  static table = null

  /**
   * Create a new model record and save it to the database.
   */
  static create(attributes = {}) {
    const model = new this(attributes)
    model.save()
    return model
  }

  /**
   * Return a relation representing all records in the database.
   */
  static get all() {
    return new Relation(this)
  }

  /**
   * Perform a query for matching models in the database.
   */
  static where(query) {
    return this.all.where(query)
  }

  /**
   * Find an existing model record in the database by the given
   * parameters.
   */
  static findBy(query) {
    return this.where(query).first
  }

  /**
   * Find an existing model record in the database by its ID.
   */
  static find(id) {
    return this.findBy({ id })
  }

  constructor(attributes = {}) {
    this.attributes = attributes
    this.errors = new Errors()
  }

  /**
   * All non-function properties of this object.
   */
  get attributes() {
    return reduce(this, (attrs, value, prop) => {
      if (typeof value !== "function") {
        attrs[prop] = value
      }

      return attrs
    }, {})
  }

  /**
   * Set attributes on this object by assigning properties directly to
   * it.
   */
  set attributes(attrs={}) {
    merge(this, attrs)
  }

  /**
   * Flatten validators from their method calls.
   */
  get validations() {
    return flatten(this.constructor.validations)
  }

  /**
   * Run all configured validators.
   */
  get valid() {
    this.validations.forEach(validation => validation.valid(this))

    return this.errors.any
  }

  /**
   * Persist the current information in this model to the database.
   */
  save() {
    if (!this.valid) {
      return false
    }

    const query = new Relation(this)

    if (this.id) {
      query.where("id", this.id).update(this.attributes)
    } else {
      query.insert(this.attributes)
    }

    query.run()

    return true
  }

  /**
   * Set the given attributes on this model and persist.
   */
  update(attributes = {}) {
    this.attributes = attributes

    return this.save()
  }

  /**
   * Remove this model from the database.
   */
  destroy() {
    const query = new Relation(this)

    query.where("id", this.id).delete()
    query.run()

    return true
  }

  /**
   * Reload this model's information from the database.
   */
  reload() {
    const model = this.constructor.find(this.id)

    merge(this, model.attributes)

    return this
  }
}

